//reducer: the way that state changes. these are functions. They accept actions and maipulate state down to components. 
import { CART_ADD_ITEM,
    CART_REMOVE_ITEM,
    CART_SAVE_SHIPPING_ADDRESS,
    CART_SAVE_PAYMENT_METHOD,
   } 
   from '../constants/cartConstants'

//??? Here I should test something for more understanding
export const cartReducer = (state = { cartItems : [] , shippingAddress:{}},action)=>{

    switch(action.type) {
        case CART_ADD_ITEM:
            console.log(state)
            
            const item = action.payload
            const existItem = state.cartItems.find(x => x.productID === item.productID )
            console.log(state.cartItems.productID)
            
            if(existItem){
                return {...state, cartItems: state.cartItems.map(x=>x.productID===existItem.productID ? item : x)}
            }else{
                return {...state, cartItems:[...state.cartItems, item]}
            }
        case CART_REMOVE_ITEM:
            console.log(state)
            return {
                ...state,
                cartItems: state.cartItems.filter(x=> x.productID !== action.payload)
            }

        case CART_SAVE_SHIPPING_ADDRESS:
            
            return {
                ...state,
                shippingAddress: action.payload
            }
        
        case CART_SAVE_PAYMENT_METHOD:
             return {
                ...state,
                paymentMethod: action.payload,
            }
        default: 
            return state
    }

}
