import { 
    PRODUCT_LIST_REQUEST,
    PRODUCT_LIST_FAIL,
    PRODUCT_LIST_SUCCESS,
    PRODUCT_DETAILS_FAIL,
    PRODUCT_DETAILS_REQUEST,
    PRODUCT_DETAILS_SUCCESS,

} 
from '../constants/productConstants'
//After creating store and reducer, I've created this action. and after that, I'll fireoff this action in Homescreen component. 
//Do what useEffect did in Homescreen component, fetch data and map through it. 
import axios from 'axios'

//the first action creator:
export const listProducts = ()=>async(dispatch)=>{
//thunk allow us to use a function(dispatch) within a function
    try{
        dispatch({type: PRODUCT_LIST_REQUEST})

        const {data} = await axios.get('/api/products')

        dispatch({
            type: PRODUCT_LIST_SUCCESS,
            payload:data
        })

    }catch(error){
        dispatch({
            type: PRODUCT_LIST_FAIL,
            payload:error.response && error.response.data.message ? error.response.data.message : error.message
        })

    }
}

export const listProductDetails = (id) => async(dispatch) => {
    try{
        dispatch({type: PRODUCT_DETAILS_REQUEST}) //SETS loading:true


        const {data} = await axios.get(`/api/products/${id}`)
        

        dispatch({
            type: PRODUCT_DETAILS_SUCCESS,
            payload:data
        })

    }catch(error){
        dispatch({
            type: PRODUCT_DETAILS_FAIL,
            payload:error.response && error.response.data.message ? error.response.data.message : error.message
        })

    }
    
}

export const createProduct = () => async (dispatch, getState) => {
    try {
      dispatch({
        type: PRODUCT_CREATE_REQUEST,
      })
  
      const {
        userLogin: { userInfo },
      } = getState()
  
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`,
        },
      }
  
      const { data } = await axios.post(`/api/products`, {}, config)
  
      dispatch({
        type: PRODUCT_CREATE_SUCCESS,
        payload: data,
      })
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message
      if (message === 'Not authorized, token failed') {
        dispatch(logout())
      }
      dispatch({
        type: PRODUCT_CREATE_FAIL,
        payload: message,
      })
    }
  }
