//actions: objects that represent the intention to change the peice of state. 
//action creators: functions that dispatch(fire off) the action.
//!example: action creator like getproducts(), in this action creator, we made a fetch request to backend to get data(via axios), and with payload, send it to reducer.
import { CART_ADD_ITEM,
         CART_REMOVE_ITEM,
         CART_SAVE_SHIPPING_ADDRESS,
         CART_SAVE_PAYMENT_METHOD,
        } 
        from '../constants/cartConstants'
        
import axios from 'axios'

export const addToCart = (id,qty) => async(dispatch,getState) => {
    const {data} = await axios.get(`/api/products/${id}`)

    dispatch({
        type:CART_ADD_ITEM,
        payload:{
            productID : data._id,
            name: data.name,
            image: data.image,
            price: data.price,
            countInStock : data.countInStock,
            qty
        }
    })

    localStorage.setItem('cartItems',JSON.stringify(getState().cart.cartItems)) //Here we save them, We will get them in store.js
}

export const removeFromCart = (id) => async(dispatch,getState)=>{
    dispatch({
        type:CART_REMOVE_ITEM,
        payload:id
    })
    localStorage.setItem('cartItems',JSON.stringify(getState().cart.cartItems)) 
}

export const saveShippingAddress = (data) => async(dispatch)=>{
    dispatch({
        type:CART_SAVE_SHIPPING_ADDRESS,
        payload:data
    })
    localStorage.setItem('shippingAddress',JSON.stringify(data)) 
}
