import React , {useEffect} from 'react'
import Message from '../components/Message'
import {Link} from 'react-router-dom'
import {Row,Col,Image,ListGroup,Button, Form,ListGroupItem, Card} from 'react-bootstrap'

import {useDispatch, useSelector} from 'react-redux'
import {addToCart} from '../actions/cartActions'

const CartPage = ({match, location, history}) => {
    //match:getting id, location: location.search for id?qty the "qty" part.Whatever after "?"  history:redirect
    const productId = match.params.id

    const qty = location.search ? Number(location.search.split('=')[1]) : 1
    //split: give us an array with 0 index of qty(of ?qty=5) and 1 index of 5.

    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart

    useEffect(()=>{
        if(productId){
            dispatch(addToCart(productId, qty))
        }
    },[dispatch,productId,qty]) 
    //if id or qty changes, causes useEffect changes.

    const removeFromCartHandler = (id)=>{
        console.log('remove')
    }

    return (
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>
                {cartItems.length === 0 
                                      ? (<Message>Your Cart is empty!<Link to="/">Go Back</Link></Message> )
                                      : (<ListGroup variant='flush'> 
                                            {cartItems.map(item=>
                                                    <ListGroup.Item key={item.productID}>
                                                        <Row>
                                                             <Col md={2}>
                                                                 <Image src={item.image} alt={item.name} fluid rounded></Image>
                                                             </Col>
                                                             <Col md={3}>
                                                                <Link to={`/product/${item.productID}`}>{item.name}</Link>
                                                             </Col>
                                                            <Col md={2}>
                                                                ${item.price}
                                                            </Col>
                                                            <Col md={2}>
                                                                            <Form.Control as='select' value={item.qty} 
                                                                                            onChange={e=>{dispatch(addToCart(item.productID, Number(e.targer.value)))}}
                                                                            >
                                                                                    {[...Array(item.countInStock).keys()].map(x=>(
                                                                                        <option key={x+1} value={x+1}>{x+1}</option>
                                                                                    ))}
                                                                            </Form.Control>
                                                             </Col>
                                                             <Col md={2}>
                                                                 <Button type='button' variant='light' onClick={()=>removeFromCartHandler(item.productID)}>
                                                                     <i className='fas fa-trash' /> 
                                                                 </Button>
                                                             </Col>
                                                        </Row>
                                                    </ListGroup.Item>)
                                            }
                                          </ListGroup> 
                                        )
                }
            </Col>
            <Col md={2}>

            </Col>
            <Col md={2}>

            </Col>
            
        </Row>
    )
}

export default CartPage
