//reducer: the way that state changes. these are functions. They accept actions and maipulate state down to components. 
import {CART_ADD_ITEM,CART_REMOVE_ITEM} from '../constants/cartConstants'

//??? Here I should test something for more understanding
export const cartReducer = (state = { cartItems : []},action)=>{

    switch(action.type) {
        case CART_ADD_ITEM:
            console.log(state)
            
            const item = action.payload
            const existItem = state.cartItems.find(x => x.product === item.product )
            
            if(existItem){
                return {...state, cartItems: state.cartItems.map(x=>x.product===existItem.product ? item : x)}
            }else{
                return {...state, cartItems:[...state.cartItems, item]}
            }
        case CART_REMOVE_ITEM:
            return {}
        default: 
            return state
    }

}
