import React , {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import {Row,Col,Image,ListGroup,Button, Form,ListGroupItem} from 'react-bootstrap'
import Rating from '../components/Rating'
import Loader from '../components/Loader'
import Message from '../components/Message'

//REDUX STUFF
import {useDispatch, useSelector} from 'react-redux'
import { listProductDetails,createProductReview} from '../actions/productActions'
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants'

//we get "match" as props, because with that we can get id from url and use it here.
const ProductPage = ({match,history}) => {

    const [qty,setQty]= useState(1)
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState('')
    //const [product, setPorduct] = useState({})
    //REDUX STUFF
    const dispatch = useDispatch()
    const productDetails = useSelector( state => state.productDetails)
    const {loading,error,product} = productDetails

    //Make sure user logged in for showing review form.
    const userLogin = useSelector((state) => state.userLogin)
    const { userInfo } = userLogin
  
    const productReviewCreate = useSelector((state) => state.productReviewCreate)
    const {
      success: successProductReview,
      loading: loadingProductReview,
      error: errorProductReview,
    } = productReviewCreate
    
   
     
    useEffect(()=>{
        dispatch(listProductDetails(match.params.id))
        // const fetchProduct = async ()=>{
        //     const {data} = await axios.get(`/api/products/${match.params.id}`)
        //     setPorduct(data) 
        // }
        
        // fetchProduct()
       

    },[dispatch,match])

    const addToCartHandler = () => {
        history.push(`/cart/${match.params.id}?qty=${qty}`)
      }
    return (

        <>
                    <Link className='btn btn-light my-3' to='/'>
                            Go Back
                    </Link>

                    {loading ? <Loader/> : error ? <Message variant='danger'>{error}</Message> :
                    (<Row>
                            <Col md={6}>
                                <Image src={product.image} alt={product.name} fluid/>
                            </Col>
                            <Col md={3}>
                                <ListGroup variant='flush'>
                                    <ListGroup.Item>
                                        <h3>{product.name}</h3>
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        <Rating 
                                            value={product.rating}
                                            text = {`${product.numReviews} reviews`}
                                        />
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        Price : ${product.price}
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        Description : ${product.description}
                                    </ListGroup.Item>

                                </ListGroup>
                            </Col>

                            <Col md={3}>
                                <ListGroup variant='flush'>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>
                                                Price : 
                                            </Col>
                                            <Col>
                                                <strong>${product.price}</strong>
                                            </Col>
                                        </Row>
                                    </ListGroup.Item> 

                                    <ListGroup.Item>
                                        <Row>
                                            <Col>
                                                Status : 
                                            </Col>
                                            <Col>
                                                {product.countInStock > 0 ? 'In Stock' : ' Out of Stock'}
                                            </Col>
                                        </Row>
                                    </ListGroup.Item> 

                                    {
                                        product.countInStock > 0 && (
                                            <ListGroupItem>
                                                <Row>
                                                    <Col>Qty</Col>
                                                    <Col>
                                                        <Form.Control as='select' value={qty} 
                                                                      onChange={e=>{setQty(e.target.value)}}
                                                        >
                                                            {[...Array(product.countInStock).keys()].map(x=>(
                                                                <option key={x+1} value={x+1}>{x+1}</option>
                                                            ))}
                                                        </Form.Control>
                                                    </Col>
                                                </Row>

                                            </ListGroupItem>
                                        )
                                    }

                                    <ListGroup.Item>
                                        <Button 
                                            onClick = { addToCartHandler}
                                            className='btn-block' 
                                            type='button' 
                                            disabled={product.countInStock===0} 
                                        >
                                            Add to Cart
                                        </Button>
                                    </ListGroup.Item> 


                                </ListGroup>
                            </Col>
                    </Row>)
            
             }
        </>
        
    )
}

export default ProductPage
